    let validationResult = [];
    let isFormValid = true;

    if (!isUsernameValid(username, specialChars, 5, 20)) {
        validationResult.push("用户名长度必须在5到20个字符之间，且不能包含特殊字符。");
        isFormValid = false;
    }

    if (!isPasswordValid(password, specialChars, 8)) {
        validationResult.push("密码长度必须至少为8个字符，且不能包含特殊字符。");
        isFormValid = false;
