    if (isFormValid) {
        let response = performLoginRequest(username, password);
        if (response.success) {
            navigateTo("upload.html");
        } else {
            validationResult.push("登录失败，请重试。");
        }
    } else {
        displayErrors(validationResult);
    }

    return isFormValid;
}

function isUsernameValid(username, specialChars, minLength, maxLength) {
    return username.length >= minLength && username.length <= maxLength && !specialChars.test(username);
}

function isPasswordValid(password, specialChars, minLength) {
    return password.length >= minLength && !specialChars.test(password);
}

