function validateForm() {
    // 定义输入变量
    var username = document.getElementById("username").value;
    var password = document.getElementById("password").value;
    var role = document.getElementById("role").value; // 假设角色选择的输入元素ID为role
    var specialChars = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;

    // 定义操作影响结果列表
    var validationResults = [];

    // 操作1：验证用户名长度
    if(username.length < 5 || username.length > 20) {
        validationResults.push("用户名长度必须在5到20个字符之间。");
    }

    // 操作2：验证密码复杂度
    if(password.length < 8 || specialChars.test(password)) {
        validationResults.push("密码长度必须至少为8个字符，且不能包含特殊字符。");
    }

    // 操作3：验证角色是否选择
    if(role === null || role === "") {
        validationResults.push("角色选择未完成。");
    }

    // 操作4：模拟登录请求
    var loginRequest = performLoginRequest(username, password);
    validationResults.push(loginRequest);

    // 返回操作影响结果列表
    return validationResults;
}

// 辅助函数，模拟登录请求
function performLoginRequest(username, password) {
    // 使用fetch API发送登录请求
    var response = fetch('/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`
    });

    // 处理响应
    return response.json().then(data => {
        if(data.success) {
            return "登录成功，跳转到文件上传页面。";
        } else {
            return "登录失败，请重试。";
        }
    }).catch(error => {
        return "登录请求失败，请稍后再试。";
    });
}
```

