function validateForm() {
    // 定义输入变量
    var username = document.getElementById("username").value;
    var password = document.getElementById("password").value;
    var role = document.getElementById("role").value; // 假设角色选择元素的id为role
    var specialChars = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;

    // 定义输出变量
    var isValidUsername = true;
    var isValidPassword = true;
    var roleSelected = true;
    var loginSuccess = false;
    var errorMessage = "";

    // 定义操作影响结果列表
    var operationResults = [];

    // 验证用户名
    if(username.length < 5 || username.length > 20 || specialChars.test(username)) {
        isValidUsername = false;
        operationResults.push("用户名验证失败");
    }

    // 验证密码
    if(password.length < 8 || specialChars.test(password)) {
        isValidPassword = false;
        operationResults.push("密码验证失败");
    }

    // 验证角色是否选择
    if(role === "") {
        roleSelected = false;
        operationResults.push("角色未选择");
    }

    // 如果输入验证未通过，显示错误信息并返回
    if(!isValidUsername || !isValidPassword || !roleSelected) {
        errorMessage = operationResults.join("; ");
        alert(errorMessage);
        return false;
    }

    // 发起登录请求
    fetch('/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}&role=${encodeURIComponent(role)}`
    })
    .then(response => response.json())
    .then(data => {
        if(data.success) {
            loginSuccess = true;
            operationResults.push("登录成功");
            // 跳转到文件上传页面
            window.location.href = 'upload.html';
        } else {
            operationResults.push("登录失败：" + data.message);
            alert('登录失败，请重试。');
        }
    })
    .catch(error => {
        operationResults.push("登录请求失败：" + error);
        alert('登录请求失败，请稍后再试。');
    });

    // 返回操作结果
    return operationResults;
}
```

