function validateForm() {
    // 获取输入值
    username = getInputValue("username");
    password = getInputValue("password");

    // 正则表达式用于检测特殊字符
    specialChars = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;

    // 定义操作影响结果列表
    operationResults = [];

    // 检查用户名长度
    if(username.length < 5 || username.length > 20) {
        operationResults.push("用户名长度必须在5到20个字符之间。");
        return false;
    }

    // 检查密码长度和是否包含特殊字符
    if(password.length < 8 || specialChars.test(password)) {
        operationResults.push("密码长度必须至少为8个字符，且不能包含特殊字符。");
        return false;
    }

    // 假设角色选择是有效的，这里不进行校验

    // 发起登录请求
    response = fetchLoginRequest(username, password);
    if(response.ok) {
        // 检查响应数据
        data = parseResponse(response);
        if(data.success) {
            operationResults.push("登录成功，跳转到文件上传页面。");
            redirectTo("upload.html");
        } else {
            operationResults.push("登录失败，请重试。");
        }
    } else {
        operationResults.push("网络或其他错误，请稍后再试。");
    }

    // 返回false以阻止表单默认提交行为
    return false;
}

function fetchLoginRequest(username, password) {
    // 这里使用fetch API模拟登录请求
    // 实际应用中需要服务器端支持
    return fetch('/login', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: `username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`
    });
}

function parseResponse(response) {
    // 解析响应为JSON
    return response.json();
}

function redirectTo(url) {
    // 跳转到指定页面
    window.location.href = url;
}
```

